import './App.css';

function App() {

  function Avatar({image, nameImage, style}) {
    return (
      <img src={image} alt={nameImage} style={style} /> 
    )
  }

  function StatusAndState({name,location}) {
    return (
      <Fragment>
      <span>{name}</span>
      <span>{location}</span>
      </Fragment>
    )
  }
  return (
    <div className='card'>
        <Avatar image="https://upload.wikimedia.org/wikipedia/commons/c/c3/Emmanuel_Macron_%28cropped%29.jpg" alt="Macron" style={{width:150 +"px"}}></Avatar>
      <div className='name-location'>
      <StatusAndState 
            name="Macron"
            location="France"
            />
      </div>
      <div className='status'>


      </div>
      <div className='state'>

        </div>
    </div>

  );
}

export default App;
