import { Fragment } from 'react';
import './App.css';

function App() {

  function Avatar({image, nameImage, style}) {
    return (
      <img src={image} alt={nameImage} style={style} /> 
    )
  }


  function Status({description}) {
    return (
      <Fragment>
      <span>{description}</span>
      </Fragment>
    )
  }
  function Texte({text}) {
    return (
      <Fragment>
      {text}
      </Fragment>
    )
  }

  return (
    <div className='card'>
        <Avatar image="https://upload.wikimedia.org/wikipedia/commons/c/c3/Emmanuel_Macron_%28cropped%29.jpg" alt="Macron" style={{width:150 +"px"}}></Avatar>
      <div className='name-location'>
        <span className='text'><Texte text="Like" /></span>

      </div>
      <div className='status'>
            <Status description="I have somthing insightfull to say, as usual."></Status>
      </div>
      <div className='state'>
            <span className='text'><Texte text="Like" /></span>
            <span className='text'><Texte text=" Reply" /></span>
            <span><Texte text=" 2 second aprÃ¨s." /></span>

        </div>
    </div>

  );
}

export default App;
