import { Fragment } from 'react';
import './App.css';

function App() {

  function Avatar({image, nameImage, style}) {
    return (
      <img src={image} alt={nameImage} style={style} /> 
    )
  }

  function NameLocation({name,location}) {
    return (
      <Fragment>
      <span>{name}</span>
      <span>{location}</span>
      </Fragment>
    )
  }

  function Status({description}) {
    return (
      <Fragment>
      <span>{description}</span>
      </Fragment>
    )
  }
  function Choice({text}) {
    return (
      <Fragment>
      {text}
      </Fragment>
    )
  }

  return (
    <div className='card'>
        <Avatar image="https://upload.wikimedia.org/wikipedia/commons/c/c3/Emmanuel_Macron_%28cropped%29.jpg" alt="Macron" style={{width:150 +"px"}}></Avatar>
      <div className='name-location'>
        <NameLocation 
            name="Macron"
            location=" France"
            />
      </div>
      <div className='status'>
            <Status description="I have somthing insightfull to say, as usual."></Status>
      </div>
      <div className='state'>
            <Choice text="Like" />
            <Choice text=" Reply" />
            <Choice text=" 2 second aprÃ¨s " />

        </div>
    </div>

  );
}

export default App;
